def merge_the_tools(string, k):
    if not string or k == 0:
        return []
    
    n = len(string)
    substrings = [string[i: i + k] for i in range(0, n, k)]

    result = []
    for string in substrings:
        seen = set()
        # this line is worth inspecting closely for learning purposes
        chars = ''.join(ch for ch in string if ch not in seen and not seen.add(ch))
        result.append(chars)
        print(chars)
    return result

if __name__ == '__main__':
    string = ""
    k = 3452
    merge_the_tools(string, k)